import React, { Component } from 'react'
import './index.css'

export default class SliderItem extends Component {
    constructor(props) {
        super(props);
        this.state={};
    }
    
    render() {
        let { count, item } = this.props;
        let width = 100 / count + '%';
        return (
            <div>
                <li className="slider-item" style={{width: width}}>
                    <img src={item.src} alt={item.alt} />
                </li>
            </div>
        )
    }
}
