import React,{useState} from 'react'
import './index.css'

export default function Footer(props) {
    const {items,nowIndex}=props;

    const[isPlay,setIsPlay]=useState(false);
    const onPlay=function () {
        setIsPlay(isPlay===false?true:false);
    }
    return (
        <div className="footer">
            <div className="song">
                <img src={items[nowIndex].picUrl} alt="album art" />
                <div className="info">
                    <p className="title">{items[nowIndex].name}</p>
                    <p className="artist">{items[nowIndex].artist}</p>
                </div>
            </div>
            <div className="controls">
                <span className="fa fa-step-backward"></span>
                <span className={"fa"+(isPlay===false?" fa-play":" fa-pause")} onClick={onPlay()}></span>
                <span className="fa fa-step-forward"></span>
                <span className="fa fa-repeat sm-hide"></span>
                <span className="fa fa-like sm-hide"></span>
            </div>
        </div>
    )
}
